boolean isCryptSolution(String[] crypt, char[][] solution) {    int[] values = new int[27];    for(char[] c : solution)        values[c[0]-65] = c[1] - 48;        long one = convert(crypt[0], values);    long two = convert(crypt[1], values);    long result = convert(crypt[2], values);        if(one == -1 | two == -1 | result == -1)        return false;        if(one + two == result)        return true;        return false;    }long convert(String s, int[] values){      long r = 0;        for(int c : s.getBytes()){        r *= 10;        r += values[c-65];    }    if(values[s.charAt(0) - 65] == 0 && s.length() > 1) return -1;    return r;}